{
  "query": "query GetDashboard($partnerId: String!) {
    dashboardPartner(partnerId: $partnerId) {
      partnerID
      nombrePartner
      totalClicks
      totalConversiones
      totalComisiones
      conversionRate
      ultimoPago
    }
  }",
  "variables": {
    "partnerId": "partner123"
  }
}

{
  "query": "query GetClicks { clicks { id idPartner idCampana urlOrigen urlDestino timestamp } }"
}

{
  "query": "mutation RegistrarClick($idPartner: String!, $idCampana: String!, $urlOrigen: String!, $urlDestino: String!) { registrarClick(idPartner: $idPartner, idCampana: $idCampana, urlOrigen: $urlOrigen, urlDestino: $urlDestino) { mensaje codigo exito } }",
  "variables": {
    "idPartner": "partner123",
    "idCampana": "campaign456",
    "urlOrigen": "https://google.com",
    "urlDestino": "https://store.com/product"
  }
}

{
  "query": "mutation RegistrarConversion($idPartner: String!, $idCampana: String!, $tipoConversion: String!, $valor: Float!, $moneda: String!) {\n    registrarConversion(idPartner: $idPartner, idCampana: $idCampana, tipoConversion: $tipoConversion, valor: $valor, moneda: $moneda) {\n      mensaje\n      codigo\n      exito\n    }\n  }",
  "variables": {
    "idPartner": "partner123",
    "idCampana": "campaign456",
    "tipoConversion": "VENTA",
    "valor": 100.50,
    "moneda": "USD"
  }
}
